import { useState } from "react";
import Female from './Female.jpeg'
import App from './App.css'
import Male from './Male.jpeg'


const Employee = () => {
  const [employees, setEmployees] = useState([
    {
      id: 1,
      name: "umair",
      sex: "male",
      designation:"full stack developer"
    },
    {
      id: 2,
      name: "john",
      sex: "male",
      designation:"backend developer"
    },
    {
      id: 3,
      name: "saif",
      sex: "male",
      designation:"React developer"
    },
    {
      id: 4,
      name: "amna",
      sex: "female",
      designation:"cyber security specialist"
    },
    {
      id: 5,
      name: "khan",
      sex: "male",
      designation:"intern"
    },
    {
      id: 6,
      name: "michael",
      sex: "male",
      designation:"full stack developer"
    },
    {
      id: 7,
      name: "serena",
      sex: "female",
      designation:"backend developer"
    },
  ]);

  return (
    <main className="container">
    <div className="row justify-content-center mt-3 mb-5">
      <div className="col-8 mb-4">
      <div className="card-collection">

      {employees.map((employee, index) => (
        <div key={employee.id} className="card m-2" style={{cursor:"pointer"}}>
          <div className="card ">

          {(<employee className="gender"></employee> === 'male')?<img src={Male} className="card-img-top" />
          : <img src={Female} className="card-img-top"/>
        }

            <div className="card-body ">
              <h5 className="card-title">Fullname: {employee.name}</h5>
              <p className="card-designation">Designation: {employee.designation}</p>
            </div>
          </div>
       
        </div>
      ))}
      </div>
      </div>
    </div>
  </main>
  
  

  )}

export default Employee;
