import { useState } from "react";
import Female from './Female.jpeg'
import './App.css' // Import CSS file correctly
import Male from './Male.jpeg'

const Employee = () => {
  const [Team, setTeam] = useState("TeamB"); // Corrected initial state

  const [employees, setEmployees] = useState([
    {
      id: 1,
      name: "umair",
      sex: "male",
      designation:"full stack developer"
    },
    // Other employee data
  ]);

  function TeamSelection(event) { // Changed parameter name to event for clarity
    const selectedTeam = event.target.value;
    console.log(selectedTeam);
    setTeam(selectedTeam); // Update Team state, not employees
  }

  return (
    <main className="container">
      <div className="row justify-content-center mt-3 mb-5">
        <div>
          <select className="form-select form-select-lg" value={Team} onChange={TeamSelection}>
            <option value="TeamA">Team A</option> {/* Changed key attribute to match the values */}
            <option value="TeamB">Team B</option>
            <option value="TeamC">Team C</option>
          </select>
        </div>

        <div className="col-8 mb-4">
          <div className="card-collection">
            {employees
              .filter(employee => employee.designation.toLowerCase().includes(Team.toLowerCase())) // Filter employees based on selected team
              .map((employee) => (
                <div key={employee.id} className="card m-2" style={{ cursor: "pointer" }}>
                  {employee.sex === 'male' ? <img src={Male} className="card-img-top" alt={`Portrait of ${employee.name}`} />
                    : <img src={Female} className="card-img-top" alt={`Portrait of ${employee.name}`} />
                  }
                  <div className="card-body">
                    <h5 className="card-title">Fullname: {employee.name}</h5>
                    <p className="card-designation">Designation: {employee.designation}</p>
                  </div>
                </div>
              ))}
          </div>
        </div>
      </div>
    </main>
  );
}

export default Employee;
