import { useState } from "react";
import Female from './Female.jpeg';
import './App.css';
import Male from './Male.jpeg';

const Employee = () => {
  const [selectedTeam, setTeam] = useState("TeamB");

  const [employees, setEmployees] = useState([
    {
      id: 1,
      name: "umair",
      sex: "male",
      designation:"full stack developer",
      TeamName:"TeamA"
    },
    {
      id: 2,
      name: "john",
      sex: "male",
      designation:"backend developer",
      TeamName:"TeamB"
    },
    {
      id: 3,
      name: "saif",
      sex: "male",
      designation:"React developer",
      TeamName:"TeamA"
    },
    {
      id: 4,
      name: "amna",
      sex: "female",
      designation:"cyber security specialist",
      TeamName:"TeamA"
    },
    {
      id: 5,
      name: "khan",
      sex: "male",
      designation:"intern",
      TeamName:"TeamC"
    },
    {
      id: 6,
      name: "michael",
      sex: "male",
      designation:"full stack developer",
      TeamName:"TeamA"
    },
    {
      id: 7,
      name: "serena",
      sex: "female",
      designation:"backend developer",
      TeamName:"TeamC"
    },
  ]);

  function TeamSelection(event) {
   
    console.log(event.target.value)
    setTeam(event.target.value);
  }

  const handleEmployeeCardClick = (event) => {
    const clickedEmployeeId = parseInt(event.currentTarget.id);
    const transformedEmployees = employees.map((employee) => {
      if (employee.id === clickedEmployeeId) {
        if (employee.TeamName === selectedTeam) {
          return { ...employee, TeamName: ' ' };
        } else {
          return { ...employee, TeamName: selectedTeam };
        }
      } else {
        return employee;
      }
    });
    setEmployees(transformedEmployees);
  }

  return (
    <main className="container">
      <div className="row justify-content-center mt-3 mb-5">
        <div>
          <select className="form-select form-select-lg" value={selectedTeam} onChange={TeamSelection}>
            <option value="TeamA">Team A</option>
            <option value="TeamB">Team B</option>
            <option value="TeamC">Team C</option>
          </select>
        </div>

        <div className="col-8 mb-4">
          <div className="card-collection">
            {employees.map((employee) => (
              <div key={employee.id} className={(employee.id === selectedTeam? 'card m-2 standout':'card m-2')} style={{ cursor: "pointer" }} onClick={handleEmployeeCardClick} id={employee.id}>
                {employee.sex === 'male' ? <img src={Male} className="card-img-top" alt={`Portrait of ${employee.name}`} />
                  : <img src={Female} className="card-img-top" alt={`Portrait of ${employee.name}`} />
                }
                <div className="card-body">
                  <h5 className="card-title">Fullname: {employee.name}</h5>
                  <p className="card-designation">Designation: {employee.designation}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </main>
  );
}

export default Employee;
