import React, { useState } from "react";

const GroupedTeamMembers = ({ employees, setTeam, selectedTeam }) => {
  const [groupedMembers, setGroupedMembers] = useState([]);

  const Allocation = () => {
    const teams = [];

    const teamData = [
      { teamName: "Team A", employees: [] },
      { teamName: "Team B", employees: [] },
      { teamName: "Team C", employees: [] },
    ];

    employees.forEach((employee) => {
      const { TeamName } = employee;
      const teamIndex = teamData.findIndex((team) => team.teamName === TeamName);
      if (teamIndex !== -1) {
        teamData[teamIndex].employees.push(employee);
      }
    });

    setGroupedMembers(teamData);
  };

  const teamClicked = (event) => {
    const clickedTeam = event.currentTarget.id;
    setTeam(clickedTeam);
  };

  return (
    <main className="container">
      {groupedMembers.map((team) => (
        <div key={team.teamName} className="card mt-2" style={{ cursor: "pointer" }}>
          <h4 id={team.teamName} className="card-header text-secondary bg-white" onClick={teamClicked}>
            Team name: {team.teamName}
          </h4>
          <div id={"collapse_" + team.teamName} className={selectedTeam === team.teamName ? "collapse show" : "collapse"}>
            <hr />
            {team.employees.map((member) => (
              <div key={member.id} className="mt-2">
                <h5 className="card-title mt-2">
                  <span className="text-dark">Full name: {member.name}</span>
                </h5>
                <p>Designation: {member.designation}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </main>
  );
};

export default GroupedTeamMembers;
