import { useState } from "react";
import Female from './Female.jpeg'
import App from './App.css'
import Male from './Male.jpeg'


const Employee = () => {
  const [Team,SetTeam]=useState("Team b")
  const [employees, setEmployees] = useState([
    {
      id: 1,
      name: "umair",
      sex: "male",
      designation:"full stack developer"
    },
    {
      id: 2,
      name: "john",
      sex: "male",
      designation:"backend developer"
    },
    {
      id: 3,
      name: "saif",
      sex: "male",
      designation:"React developer"
    },
    {
      id: 4,
      name: "amna",
      sex: "female",
      designation:"cyber security specialist"
    },
    {
      id: 5,
      name: "khan",
      sex: "male",
      designation:"intern"
    },
    {
      id: 6,
      name: "michael",
      sex: "male",
      designation:"full stack developer"
    },
    {
      id: 7,
      name: "serena",
      sex: "female",
      designation:"backend developer"
    },
  ]);

function TeamSelection(events){
  console.log(events.target.value) // target.value retrieve the value of the button or option 
  setEmployees(events.target.value)
}

function addRemove(event){
employees.map((employee)=>{
  if(employee.id === setEmployees(event.target.value)){
setEmployees(event.target.id)
  }else{
    
  }
})
}


  return (
    <main className="container">
  <div className="row justify-content-center mt-3 mb-5">
    <div>
      <select className="form-select form-select-lg" value={Team} onChange={TeamSelection} onClick={addRemove}>
        <option value="TeamA">Team A</option>
        <option value="TeamB">Team B</option>
        <option value="TeamC">Team C</option>
      </select>
    </div>
    <div className="col-8 mb-4">
      <div className="card-collection">
        {employees.map((employee) => (
          <div key={employee.id} className="card m-2" style={{ cursor: "pointer" }}>
            {employee.sex === 'male' ? <img src={Male} className="card-img-top" alt={`Portrait of ${employee.name}`} />
              : <img src={Female} className="card-img-top" alt={`Portrait of ${employee.name}`} />
            }
            <div className="card-body">
              <h5 className="card-title">Fullname: {employee.name}</h5>
              <p className="card-designation">Designation: {employee.designation}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  </div>
</main>

  
  

  )}

export default Employee;
