// App.js
import React,{useState,useEffect} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Employee from "./Employee";

function App() {
  const [selectedTeam, setTeam] = useState("TeamB");

  const [employees, setEmployees] = useState([
    {
      id: 1,
      name: "umair",
      sex: "male",
      designation: "full stack developer",
      TeamName: "TeamA",
    },
    {
      id: 2,
      name: "john",
      sex: "male",
      designation: "backend developer",
      TeamName: "TeamB",
    },
    {
      id: 3,
      name: "saif",
      sex: "male",
      designation: "React developer",
      TeamName: "TeamA",
    },
    {
      id: 4,
      name: "amna",
      sex: "female",
      designation: "cyber security specialist",
      TeamName: "TeamA",
    },
    {
      id: 5,
      name: "khan",
      sex: "male",
      designation: "intern",
      TeamName: "TeamC",
    },
    {
      id: 6,
      name: "michael",
      sex: "male",
      designation: "full stack developer",
      TeamName: "TeamA",
    },
    {
      id: 7,
      name: "serena",
      sex: "female",
      designation: "backend developer",
      TeamName: "TeamC",
    },
  ]);

  useEffect(()=>{
localStorage.setItem('employee-list' , JSON.stringify(employees))
  },[employees])

  useEffect(()=>{
    localStorage.setItem('Selected team' , JSON.stringify(employees))
      },[selectedTeam])

  function TeamSelection(event) {
    console.log(event.target.value);
    setTeam(event.target.value);
  }

  const handleEmployeeCardClick = (event) => {
    const clickedEmployeeId = parseInt(event.currentTarget.id);
    const transformedEmployees = employees.map((employee) => {
      if (employee.id === clickedEmployeeId) {
        if (employee.TeamName === selectedTeam) {
          return { ...employee, TeamName: " " };
        } else {
          return { ...employee, TeamName: selectedTeam };
        }
      } else {
        return employee;
      }
    });
    setEmployees(transformedEmployees);
  };
  return (
    <div>
      <Header 
      selectedTeam={selectedTeam}

      TotalMembers ={employees.filter((member)=> member.TeamName === selectedTeam ).length}
      
      
      />
      <Employee  
                 
                 
                 employees={employees}
                 
                 selectedTeam={selectedTeam}
             handleEmployeeCardClick={handleEmployeeCardClick}
             TeamSelection={TeamSelection}
      
      
      
      />
      <Footer />
    </div>
  );
}

export default App;
