import { useState } from "react";
import Female from './Female.jpeg'


const Employee = () => {
  const [employees, setEmployees] = useState([
    {
      id: 1,
      name: "umair",
      company: "netflix",
      sex: "male"
    },
    {
      id: 2,
      name: "john",
      company: "meta",
      sex: "male"
    },
    {
      id: 3,
      name: "saif",
      company: "google",
      sex: "male"
    },
    {
      id: 4,
      name: "amna",
      company: "netflix",
      sex: "female"
    },
    {
      id: 5,
      name: "khan",
      company: "google",
      sex: "male",
      designation:"intern"
    }
  ]);

  return (
    <main className="container">
    <div className="row">
      {employees.map((employee, index) => (
        <div key={employee.id} className="col-lg-4 mb-4">
          <div className="card">
            <img src={Female} className="card-img-top" alt={`Portrait of ${employee.name}`} />
            <div className="card-body">
              <h5 className="card-title">Fullname: {employee.name}</h5>
              <h6 className="card-designation">Designation: {employee.designation}</h6>
            </div>
          </div>
          {(index + 1) % 3 === 0 && <div className="w-100 d-lg-none"></div>}
        </div>
      ))}
    </div>
  </main>
  
  

  )}

export default Employee;
